import React, { Component } from 'react';

export default class C extends Component {
    constructor(props) {
        super(props);
        this.state = {
          tgl: new Date().toLocaleString()
        };
      }
    
      getDate() {
        var date = { currentTime: new Date().toLocaleString() };
    
        this.setState({
          date: date
        });
      }
      componentDidMount() {         
        alert(`Bhaa!!`)     
      } 
    
      render() {
        return (
          <div>
          <center>
            <p> Waktu Server : {this.state.tgl}</p>
          </center>
          </div>
        );
      }
}
